#!./usr/bin/python3
import gpt_2_simple as gpt2
import warnings
warnings.filterwarnings("ignore", category=FutureWarning)

binary_input = ""
sess = gpt2.start_tf_sess()
gpt2.load_gpt2(sess)

gpt2.generate(sess,
              length=20,
              temperature=.7,
              prefix=binary_input,
              nsamples=3
              )